ViewAttribute.TransientExpression:
if (ProductCategoryDesc != null) {
return ProductCategoryDesc + '.' + ProductClassDesc + '.' + ProductBrandDesc + '.' + ProductExtDesc + '.' + ProductPackDesc;
} else {
if (ProductCategory == 'CT001') {
return 'Food' + '.' + ProductClassDesc + '.' + ProductBrandDesc + '.' + ProductExtDesc + '.' + ProductPackDesc;
} else if (ProductCategory == 'CT002') {
return 'Non Food' + '.' + ProductClassDesc + '.' + ProductBrandDesc + '.' + ProductExtDesc + '.' + ProductPackDesc;
} else {
return ProductClassDesc + '.' + ProductBrandDesc + '.' + ProductExtDesc + '.' + ProductPackDesc;
}
}

